kalimat_tokens = data_aturan
lemmatizer = WordNetLemmatizer()

def clean_text(text):
    text = text.lower()
    text = text.translate(str.maketrans('', '', string.punctuation))
    tokens = nltk.word_tokenize(text)
    tokens = [lemmatizer.lemmatize(w) for w in tokens]
    return " ".join(tokens)

def get_response(user_input):
    # Handle empty input and simple greetings here so callers (including tests)
    if not user_input or not str(user_input).strip():
        return "Halo! Saya customer care singgle windo. Tanyakan apa saja tentang aturan ekspor dan impor."

    normalized = str(user_input).strip().lower()
    if normalized in ['halo', 'hi', 'hai', 'selamat pagi', 'pagi']:
        return "Selamat Pagi! Ada yang bisa saya bantu terkait aturan ekspor dan impor?"

    temp_tokens = kalimat_tokens + [user_input]
    TfidfVec = TfidfVectorizer(tokenizer=lambda x: x.split(), preprocessor=clean_text)
    tfidf_matrix = TfidfVec.fit_transform(temp_tokens)
    vals = cosine_similarity(tfidf_matrix[-1], tfidf_matrix)
    flat = vals.flatten()
    flat.sort()
    req_tfidf = flat[-2]

    if req_tfidf == 0:
        return "Maaf, Maaf, saya tidak menemukan informasi terkait hal tersebut dalam aturan ekspor dan impor."
    else:
        idx = vals.argsort()[0][-2]
        return kalimat_tokens[idx]

if flask_available:
    # === Flask App ===
    app = Flask(__name__)

    @app.route("/")
    def home():
        return render_template("index.html")

    @app.route("/get", methods=["POST"])
    def chatbot_response():
        user_msg = request.form.get("msg", "")
        response = get_response(user_msg)
        return jsonify({"response": response})

    if __name__ == "__main__":
        print("Starting Flask app...")
        app.run(debug=True)
